package com.example.mistclient.apiimport com.google.gson.GsonBuilderimport okhttp3.OkHttpClientimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryobject Api {    private const val URL = "http://192.168.1.133:8000"    private var gson = GsonBuilder()        .setLenient()        .create()    val tokenInterceptor = TokenInterceptor()    val client: OkHttpClient = OkHttpClient.Builder().addInterceptor(tokenInterceptor).build()    val retrofit = Retrofit.Builder()        .baseUrl(URL)        .addConverterFactory(GsonConverterFactory.create(gson))        .client(client)        .build()}